import { useState } from 'react'

function Barbecue(){
const [adults, setAdults] = useState()

function calculation(){
  console.log(setAdults(adults))
}

function meatPerPerson(duration){
  if(duration >= 6){
    return 650
  }else{
    return 400
  }
}

function beerPerPerson(duration){
  if(duration >= 6){
    return 2000
  }else{
    return 1200
  }
}

function drinkPerPerson(duration){
  if(duration >= 6){
    return 1500
  }else{
    return 1000
  }
}

return(
    <div className='container'>
        <h1 className='barbecue'>Barbecue</h1>
        <input className='inputGlobal' id='adults' name='adults' type='number' placeholder='Adults' value={adults} />
        <button className='calculationGlobal'>Calculation</button>
        <div id='result'></div>
      </div>
)

}

export default Barbecue
