import { useState } from 'react'

function Barbecue(){
const [adults, setAdults] = useState([])
const [adult, setAdult] = useState()

const [kids, setKids] = useState([])
const [kid, setKid] = useState()

const [resultado, setResultado] = useState()

function calculation(){
  setKids([kids,kid])
  setAdults([adults, adult])
  const restAdults = adults.shift(adult => ({adult}))
  const restKids = kids

  setResultado(console.log(restAdults - restKids))
}


return(
    <div className='container'>
        <h1 className='barbecue'>Barbecue</h1>
        <input className='inputGlobal' id='adults' value={adult} onChange={(e)=>setAdult(e.target.value)} name='adults' type='number' placeholder='Adults'  />
        <input className='inputGlobal' id='kids' value={kids} onChange={(e)=>setKid(e.target.value)} name='kids' type='number' placeholder='Kids'  />
        <button className='calculationGlobal' onClick={calculation}>Calculation</button>
        {resultado}
      </div>
)

}

export default Barbecue
